# transforming dataimport pandas as pddfiris = pd.read_csv("iris.csv")# don't do this # print(dfiris.columns)# do this col_names = dfiris.columns.tolist()print(col_names)#  adding a col and transforming current col (and keeping it)dfiris["sepal_length_sq"] = dfiris["sepal_length"].apply(lambda x: x**2)# grouping grouped = dfiris.groupby("class")mean_square_values = grouped['sepal_length_sq'].mean()print(mean_square_values)# working with multiple files (making it one)df1 = pd.read_csv("person_split1.csv")df2 = pd.read_csv("person_split2.csv")df3 = pd.concat([df1,df2], ignore_index = True)# working with data without matching colsdfed = pd.read_csv("person_education.csv")dfwork = pd.read_csv("person_work.csv")# inner joindf_merge_inner = pd.merge(dfed, dfwork, on = 'id')# merge function in pandas, default is an inner join# outer joun = A full outer join returns all the rows from the left Dataframe, # and all the rows from the right Dataframe, # and matches up rows where possible, with NaNs elsewhere# replacing part of data within a col (removing unnecessary info)dfiris["class"] = dfiris["class"].str.replace("Iris-", "")# getting only lenghts above 5dfiris = dfiris[dfiris["sepal_length"]>5] dfiris = dfiris[dfiris['class'] == "-virginica"]dfiris.to_csv("cleaned_iris.csv")